import { useState,useEffect } from "react";
import MovieList from "./movielist/MovieList";
import MoviesByGenres from "./movies-genres/MoviesByGenres";
import MoviesByTitle from "./movies-title/MoviesByTitle";
import PubSub from "pubsub-js";

export default function Movie(){
  const [selectObj,setSelectObj] = useState({
    isMovieList: false,
    isMoviesByGenres: false,
    isMoviesByKeyword: false
  });

  useEffect(() => {
    PubSub.subscribe("select", (_,selectedVal)=>{
      const newSelectObj = {};
      if(selectedVal === "genres"){
        newSelectObj.isMoviesByGenres = true;
        setSelectObj(newSelectObj);
      }else if(selectedVal === "keywords"){
        newSelectObj.isMoviesByKeyword = true;
        setSelectObj(newSelectObj);
      }else{
        newSelectObj.isMovieList = true;
        setSelectObj(newSelectObj);
      }
    })
  },[])

  return (
    <div>
      {
        selectObj.isMovieList ? <MovieList /> :
        selectObj.isMoviesByGenres ? <MoviesByGenres /> :
        selectObj.isMoviesByKeyword ? <MoviesByTitle /> :
        null
      }
    </div>
  )
}
