import { useState,useEffect } from "react";
import MovieList from "./movielist/MovieList";
import MoviesByGenres from "./movies-genres/MoviesByGenres";
import MoviesByTitle from "./movies-title/MoviesByTitle";
import PubSub from "pubsub-js";

export default function Movie(){
  const [selectObj,setSelectObj] = useState({
    all: false,
    genres: false,
    keywords: false
  });
  const [inputVal,setInputVal] = useState("");

  useEffect(() => {
    PubSub.subscribe("select", (_,selectedObj)=>{ //{selectValue,inputValue}
      const newSelectObj = {};
      if(selectedObj.se) {
        newSelectObj[selectedVal] = true;
      }else{
        newSelectObj.all = true;
      }
      setSelectObj(newSelectObj);
    })
  },[])

  return (
    <div>
      {
        selectObj.all ? <MovieList /> :
        selectObj.genres ? <MoviesByGenres /> :
        selectObj.keywords ? <MoviesByTitle /> :
        null
      }
    </div>
  )
}
