import { useState } from "react";
import "./SignIn.css";

export default function SignIn(){
  const [user,setUser] = useState({
    username: "",
    password: ""
  })

  const getInfo = (e,userinfo)=>{
    user[userinfo] = e.current.value;
    setUser(user);
  }
  const handleClick = ()=>{
    console.log(user.username);
    console.log(user.password);
  }

  return (
    <div className="sign-in">
      <h1>Sign In</h1>
      <div>
        <input placeholder="Username" onChange={(e)=>getInfo("username")} />
      </div>
      <div>
        <input placeholder="Password" onChange={(e)=>getInfo("password")} />
      </div>
      <button className="pin" onClick={handleClick}>Sign In</button>
      <div className="sign-register">New to Clover App? <span>Sign up now.</span></div>
    </div>
  )
}
