import { useState,useEffect } from "react";
import axios from "axios";
import "./Movie.css";

export default function Movie(){
  const [genres,setGenres] = useState(["action","comedy","drama","fantasy","horror","mystery","romance"]);
  // const [genresLoaded,setGenresLoaded] = useState(false);
  const [genresObj, setGenresObj] = useState({
    "action": [],
    "comedy": [],
    "drama": [],
    "fantasy": [],
    "horror": [],
    "mystery": [],
    "romance": []
  })

  useEffect(() => {
    axios.get("http://localhost:8888/genres").then((response)=>{
      console.log("response data:", response.data);
      setGenres(response.data);
      // setGenresLoaded(true);
      // const newGenresObj = {};
      // Object.assign(newGenresObj,genresObj);
      const newGenresObj = Object.assign({},genresObj);
      // const newGenresObj = structuredClone(genresObj);

      response.data.forEach((genres)=>{
        axios.get(`http://localhost:8888/movies/genres/${genres.name}`).then((response)=>{
          console.log("genres name:", genres.name);
          // const newGenresObj = {...genresObj};
          newGenresObj[genres.name] = response.data;
          console.log("genresObj", newGenresObj);
          setGenresObj(newGenresObj);
        })
      });
    });
  },[]);
  // useEffect(() => {
  //   if(genres && genresLoaded) {
  //     // const newGenresObj = JSON.parse(JSON.stringify(genresObj));
  //     const newGenresObj = Object.assign({},genresObj);
  //     genres.forEach((genres)=>{
  //       console.log("genres movies: ", genres);
  //       axios.get(`http://localhost:8888/movies/genres/${genres.name}`).then((response)=>{
  //         newGenresObj[genres.name] = response.data;
  //         console.log("genres name:", genres.name);
  //         console.log("response data:", response.data);
  //         console.log("genresObj", newGenresObj);
  //         setGenresObj(newGenresObj);
  //       })
  //     });
  //   }
  // },[genres,genresLoaded])

  return (
    <div className="movie-container">
      {
        genres && genres.map((genres)=>{
          return <div className="genres clearfix" key={genres.id}>
            <div className="genres-name">{genres.name}</div>
            <ul>
              {
                genresObj[genres.name] && genresObj[genres.name].map((movie)=>{
                  return <li key={movie.id} className="movie-list">
                    <img src={require(`../../images/${movie.imgUrl}`)} alt="movie-pic" />
                    <div>{movie.name}</div>                  
                  </li>
                })
              }
            </ul>
          </div>
        })
      }
    </div>
  )
}
