import { useState,useEffect } from "react";
import "./Header.css";
import PubSub from "pubsub-js";
import { useNavigate } from "react-router-dom";

export default function Header(){
  const [selectValue,setSelectValue] = useState("all");
  const [inputValue, setInputValue] = useState("");
  const [signIn,setSignIn] = useState(false);
  const [username,setUsername] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    PubSub.publish("signIn", signIn);
  },[signIn]);
  useEffect(() => {
    PubSub.subscribe("username", (_,data)=>{
      console.log("Header username", data);
      setUsername(data);
    })
  })

  const handleSelect = (e) => {
    setSelectValue(e.target.value);
  }
  const handleInput = (e) => {
    setInputValue(e.target.value);
  }
  const handleClick = () => {
    selectValue==="all" ? navigate("/") :
    selectValue==="genres" ? navigate(`/genres/${inputValue}`) :
    selectValue==="keywords" ? navigate(`/keywords/${inputValue}`) :
    navigate("*");
    setInputValue("");
  }
  const handleSignIn = () => {
    setSignIn(true);
  }

  return (
    <div className="header">
      <div className="logo-container">
        <img className="logo" src={require("../../images/clover.png")} alt="clover"/>
        <span className="logo-span">Clover App</span>
      </div>

      <select value={selectValue} onChange={handleSelect}>
        <option value="all">all</option>
        <option value="genres">genres</option>
        <option value="keywords">keywords</option>
      </select>
      <input className="search" placeholder="Search Movies" value={inputValue} onChange={handleInput} />
      <button className="logo-search" onClick={handleClick}>
        <img className="search-img" src={require("../../images/search.png")} alt="search"/>
      </button>

      <span className="cart sign-container" >Cart</span>
      <img className="cart-img" src={require("../../images/cart.png")} alt="cart"/>
      {
        username==="" ? 
        <span className="sign sign-container" onClick={handleSignIn}>Sign In</span> : 
        <span className="sign sign-container">Welcome {username.charAt(0).toUpperCase()+username.substring(1)}!</span>
      }  
    </div>
  )
}

