import { useState,useEffect,useRef } from "react";
import axios from "axios";
import "./Movie.css";

export default function Movie(){
  const [genres,setGenres] = useState([]);
  const [count, setCount] = useState(0);
  const [indexObj, setIndexObj] = useState({
    action: 0,
    comedy: 0,
    drama: 0,
    fantasy: 0,
    horror: 0,
    mystery: 0,
    romance: 0
  });
  const [genresObj, setGenresObj] = useState({
    action: [],
    comedy: [],
    drama: [],
    fantasy: [],
    horror: [],
    mystery: [],
    romance: []
  })
  // const [action,setAction] = useState([])
  // const [comedy,setComedy] = useState([])
  // const [drama,setDrama] = useState([])
  // const [fantasy,setFantasy] = useState([])
  // const [horror,setHorror] = useState([])
  // const [mystery,setMystery] = useState([])
  // const [romance,setRomance] = useState([])
  const movieList = useRef({});

  useEffect(() => {
    axios.get("http://localhost:8888/genres").then((response)=>{
      setGenres(response.data);
    });
  },[]);
  useEffect(() => {
    if(genres.length>0 && count<3) {
      const newGenresObj = Object.assign({},genresObj);
      genres.forEach((genres)=>{
        console.log("genres movies: ", genres);
        axios.get(`http://localhost:8888/movies/genres/${genres.name}`).then((response)=>{
            newGenresObj[genres.name] = response.data;
            setGenresObj(newGenresObj);
            setTimeout(()=>{
              setCount(count+1);
            },500)
            // const children = movieList.current[genres.name].children;
            // movieList.current[genres.name].style.width = 240*children.length + "px";
            // console.log("genres name: ", genres.name);
            // console.log("style width: ", movieList.current[genres.name].style.width);
          })
      });
    }
  },[genres,genresObj,count])

  const getPrev = (dire, genresName)=>{
    const styles = getComputedStyle(movieList.current[genresName]);
    const movieListLeft = styles.left;
    const leftValue = parseInt(movieListLeft);
    console.log("leftValue: ", leftValue);
    console.log("genres name: ", genresName);
    console.log("index: ", indexObj[genresName]);
    const children = movieList.current[genresName].children;
    const newIndexObj = Object.assign({}, indexObj);
    if(dire === "pre"){
      if(children.length-indexObj[genresName] > 5) {
        movieList.current[genresName].style.left = leftValue - 240 + "px";
        newIndexObj[genresName] = indexObj[genresName] + 1;
        setIndexObj(newIndexObj);
      }
    }
    if(dire === "next"){
      if(indexObj[genresName] > 0) {
        movieList.current[genresName].style.left = leftValue + 240 + "px";
        newIndexObj[genresName] = indexObj[genresName] - 1;
        setIndexObj(newIndexObj);
      }
    }
  }

  return (
    <div className="movie-container">
      {
        genres.length>0 && genres.map((genres)=>{
          return <div className="genres clearfix" key={genres.id}>
            {
              genresObj[genres.name].length>0 ? 
              <div className="genres-name">{genres.name}</div> :
              null
            }
            <ul ref={(node)=>{movieList.current[genres.name] = node;}} className="movie-ul">
              {
                genresObj[genres.name].length>0 && genresObj[genres.name].map((movie)=>{
                  return <li key={movie.id} className="movie-list">
                    <img src={require(`../../images/${movie.imgUrl}`)} alt="movie-pic" />
                    <div>{movie.name}</div>                  
                  </li>
                })
              }
            </ul>
            {
              genresObj[genres.name].length>0 ? 
              <div>
                <div className="pre-arrow arrow" onClick={()=>{getPrev("pre", genres.name)}}>
                  <img src={require(`../../images/arrow.jpeg`)} alt="movie-pic" />
                </div>
                <div className="post-arrow arrow" onClick={()=>{getPrev("next", genres.name)}}>
                  <img src={require(`../../images/arrow.jpeg`)} alt="movie-pic" />
                </div>
              </div>:
              null
            }

          </div>
        })
      }
    </div>
  )
}
