  },[])
  return (
    <div className="movie-detail">
      <h2 className="title">{props.movie.name}</h2>
      <div className="clearfix">
        <div className="year">{props.movie.releasedYear} -</div>
        <div className="minutes">{props.movie.minutes}m</div>
        <div className="rate">
          <div>rating:</div>
          <div>⭐️ <span>{props.movie.rate}</span>/10</div>
        </div>
      </div>
      <div className="img"><img src={require(`../../../images/${props.movie.imgUrl}`)} alt="movie-pic" /></div>
      <div className="detail-genres">
        {
          props.movie.genres.map((genres) => {
            return <button key={genres.id}>
              {genres.name}
            </button>
          })
        }
      </div>
      <div className="desc">{props.movie.description}</div>
      <button className="pin">Add to Cart</button>
    </div>
  )
}
