import { useState,useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from 'axios';

export default function MovieUpdate() {
  const params = useParams();
  const [movie,setMovie] = useState(null);

  useEffect(()=>{
    axios.get(`http://localhost:8888/movies/movie/${params.mid}`).then((response)=>{
      setMovie(response.data);
      console.log("data: ", response.data);
    })
  },[])

  return (
    // {
      movie===null ? 
      null :
      // <div>hello</div>
      <div className="sign-in">
        <h1>Update Movie</h1>
        <div>
          <input type="text" placeholder={movie.name} />
        </div>
        <div>
          <input type="text-area" placeholder={movie.description} />
        </div>
        <div>
          <input type="text" placeholder={movie.imgUrl} />
        </div>
        <div>
          <input type="number" placeholder={movie.minutes} />
        </div>
        <div>
          <input type="number" placeholder={movie.releasedYear} />
        </div>
        <div>
          <input type="text" placeholder={movie.rate} />
        </div>
        <div>
          <input type="text" placeholder={movie.isAvailable.toString()} />
        </div>
        <div>
          <input type="text" placeholder={movie.genres.map((g)=>{return g.name})} />
        </div>
      </div>

    // }
  )
}
