import { useState,useEffect,useRef } from "react";
import axios from 'axios';
import MovieDetail from "../moviedetail/MovieDetail";

export default function MoviesByGenres(props){
  const [keywordsMovies,setKeywordsMovies] = useState([]);
  const [isDetailed,setIsDetailed] = useState(false);
  const [movie, setMovie] = useState({});
  const movieListRef = useRef();

  useEffect(() => {
    axios.get(`http://localhost:8888/movies/name/${props.searchValue}`).then(
      response => {
        setKeywordsMovies(response.data);
      },
      error => {
        console.log(error.message);
      }
    )
  },[])
  useEffect(() => {
    if(keywordsMovies && keywordsMovies.length>0){
      console.log("ref: ", movieListRef.current);
      const len = movieListRef.current.children.length;
      if(len < 5) {
        movieListRef.current.style.height = 800 + "px";
      }
    }
  },[keywordsMovies])

  const showDetail = (movie)=>{
    setIsDetailed(true);
    setMovie(movie);
  }

  return (
    isDetailed ? 
    <MovieDetail movie={movie} /> : 
    <div className="movie-genres">
      {
        keywordsMovies.length===0 ? 
        <div className="none-movie">No Movies Found! Please try again.</div> :
        <div>
          <div className="list-genres">Movies with keywords: {props.searchValue}</div>
          <div className="movie-top clearfix">
            <span>Title</span>
            <span>Rating</span>
          </div>
          <div ref={movieListRef} className="keywords-list">
            {
              keywordsMovies.map((movie,index) => {
                return <div className="movie-item" key={index} onClick={()=>showDetail(movie)}>
                  <img className="movie-info img" src={require(`../../../images/${movie.imgUrl}`)} alt="movie-pic" />
                  <div className="movie-info title">{index+1}. {movie.name}</div>
                  <div className="movie-info year">({movie.releasedYear})</div>
                  <div className="movie-info rate">⭐️&nbsp;  
                    {
                      movie.rate.toString().length<2 ?
                      movie.rate+".0" :
                      movie.rate
                    }
                  </div>
                </div>
              })    
            }
          </div>
        </div>
      }
    </div>
  )
}
