import { useState,useEffect } from "react";
import axios from "axios";
import "./Movie.css";

export default function Movie(){
  const [genres,setGenres] = useState([]);
  const [genresObj, setGenresObj] = useState({
    "action": [],
    "comedy": [],
    "drama": [],
    "fantasy": [],
    "horror": [],
    "mystery": [],
    "romance": []
  })

  useEffect(() => {
    axios.get("http://localhost:8888/genres").then((response)=>{
      setGenres(response.data);
      // response.data.forEach((genres)=>{
      //   axios.get(`http://localhost:8888/movies/genres/${genres.name}`).then((response)=>{
      //     const newGenresObj = genresObj;
      //     newGenresObj[genres.name] = response.data;
      //     setGenresObj(newGenresObj);
      //   })
      // });
    });
  },[]);
  useEffect(() => {
    if(genres && genres.length>0) {
      genres.forEach((genres)=>{
        axios.get(`http://localhost:8888/movies/genres/${genres.name}`).then((response)=>{
          const newGenresObj = genresObj;
          newGenresObj[genres.name] = response.data;
          setGenresObj(newGenresObj);
        })
      });
    }
  },[genres])

  return (
    <div className="movie-container">
      {
        genres.map((genres)=>{
          return <div className={genres.name} key={genres.id}>
            <div className="genres">{genres.name}</div>
            <ul>
              {
                genresObj[genres.name].map((movie)=>{
                  return <li key={movie.id}>{movie.name}</li>
                })
              }
            </ul>
          </div>
        })
      }
    </div>
  )
}
