import { useState,useEffect } from "react";
import axios from "axios";
import "./Movie.css";

export default function Movie(){
  const [genres,setGenres] = useState([]);
  const [action,setAction] = useState([]);
  const [comedy,setComedy] = useState([]);
  const [drama,setDrama] = useState([]);
  const [fantasy,setFantasy] = useState([]);
  const [horror,setHorror] = useState([]);
  const [mystery,setMystery] = useState([]);
  const [romance,setRomance] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8888/genres").then((response)=>{
      console.log(response.data);
      setGenres(response.data);
    });
  },[]);
  useEffect(()=>{
    // if(genres && genres.length>0){
    if(genres){
        genres.forEach((genres)=>{
        axios.get(`http://localhost:8888/movies/genres/${genres.name}`).then((response)=>{
          console.log("genres name: ", genres.name);
          switch(genres.name){
            case "action":
              console.log("action");
              setAction(response.data);
              break;
            case "comedy":
              setComedy(response.data);
              break;
            case "drama":
              setDrama(response.data);
              break;
            case "fantasy":
              setFantasy(response.data);
              break;
            case "horror":
              setHorror(response.data);
              break;
            case "mystery":
              setMystery(response.data);
              break;
            case "romance":
              setRomance(response.data);
              break;
            default:
              break;
          }
        })
      });
    }
  },[genres]);

  return (
    <div className="movie-container">
      <div className="action">
        {
          action.map((movie)=>{
            return <li key={movie.id}>{movie.name}</li>
          })
        }
      </div>
      <div className="comedy">
        {
          comedy.map((movie)=>{
            return <li key={movie.id}>{movie.name}</li>
          })
        }
      </div>
    </div>
  )
}
