import { useState,useEffect } from "react";
import MovieList from "./movielist/MovieList";
import MoviesByGenres from "./movies-genres/MoviesByGenres";
import MoviesByTitle from "./movies-title/MoviesByTitle";
import PubSub from "pubsub-js";

export default function Movie(){
  const [selectObj,setSelectObj] = useState({
    all: false,
    genres: false,
    keywords: false
  });
  const [inputObj,setInputObj] = useState({
    genres: "",
    keywords: ""
  })

  useEffect(() => {
    PubSub.subscribe("select", (_,data)=>{ //{selectValue,inputSubmit}
      const {selectValue,inputSubmit} = data;
      const newSelectObj = {};
      const newInputObj = {};
      if(selectValue) {
        newSelectObj[selectValue] = true;
        newInputObj[selectValue] = inputSubmit;
      }else{
        newSelectObj.all = true;
      }
      setSelectObj(newSelectObj);
      setInputObj(newInputObj);
    })
  },[])

  return (
    <div>
      {
        selectObj.all ? <MovieList /> :
        selectObj.genres ? <MoviesByGenres searchValue={inputObj.genres} /> :
        selectObj.keywords ? <MoviesByTitle searchValue={inputObj.keywords} /> :
        null
      }
    </div>
  )
}
