import { useState } from "react";
import axios from "axios";
import "./SignIn.css";

export default function SignIn(){
  const [user,setUser] = useState({
    username: "",
    password: ""
  })
  const [userData,setUserData] = useState({})

  const getInfo = (e,userinfo)=>{
    user[userinfo] = e.target.value;
    setUser(user);
  }
  const handleClick = ()=>{
    console.log("username: ", user.username);
    console.log("password: ", user.password);
    // setUserData(user);
    // axios.get(`http://localhost:8888/movies/genres/${props.searchValue}`).then(
    axios.get(`http://localhost:8888/user/${user.username}/${user.password}`).then(response=>{
      console.log("user from data: ", response.data);
      setUserData(response.data);
    })
  }

  return (
    <div className="sign-in">
      <h1>Sign In</h1>
      <div>
        <input placeholder="Username" onChange={(e)=>getInfo(e,"username")} />
      </div>
      <div>
        <input placeholder="Password" onChange={(e)=>getInfo(e,"password")} />
      </div>
      <button className="pin" onClick={handleClick}>Sign In</button>
      <div className="sign-register">New to Clover App? <span>Sign up now.</span></div>
    </div>
  )
}
