import { useState,useEffect } from "react";
import axios from 'axios';

export default function MoviesByGenres(props){
  const [genresMovies,setGenresMovies] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:8888/movies/genres/${props.genres.trim()}`).then(
      response => {
        setGenresMovies(response.data);
      },
      error => {
        console.log(error.message);
      }
    )
  },[])

  return (
    <div className="movie-container">
      movie list by genres...
      {
        genresMovies.map((movie) => {
          return <div className="movie-item">
            <img className="movie-info img" src={require(`../../../images/${movie.imgUrl}`)} alt="movie-pic" />
            <div className="movie-info title">{movie.name}</div>
            <div className="movie-info year">{movie.releasedYear}</div>
            <div className="movie-info rate">{movie.rate}</div>
          </div>
        })
      }
    </div>
  )
}
