import { useState } from "react";
import axios from "axios";
// import PubSub from "pubsub-js";
import "./SignIn.css";

export default function SignIn(){
  const [userInput,setUserInput] = useState({
    username: "",
    password: ""
  })
  // const [user,setUser] = useState({})
  const [isErrorUser,setIsErrorUser] = useState(false);

  const getInfo = (e,userinfo)=>{
    userInput[userinfo] = e.target.value;
    setUserInput(userInput);
  }
  const handleClick = ()=>{
    console.log("username: ", userInput.username);
    console.log("password: ", userInput.password);
    axios.get(`http://localhost:8888/user/${userInput.username}/${userInput.password}`).then(
      response=>{
        console.log("user from data: ", response.data);
        // setUser(response.data);
        setIsErrorUser(false);
        // PubSub.publish("username",response.data.username);
        localStorage.setItem("user", response.data.username);
      },
      error => {
        console.log("error: ", error.message);
        setIsErrorUser(true);
      }
    )
  }

  return (
    <div className="sign-in">
      <h1>Sign In</h1>
      {
        isErrorUser ? 
        <div>
          <button>Sorry, we can't find an account with this username and password. Please try again or create a new account.</button>
        </div> :
        null
      }
      <div>
        <input placeholder="Username" onChange={(e)=>getInfo(e,"username")} />
      </div>
      <div>
        <input placeholder="Password" onChange={(e)=>getInfo(e,"password")} />
      </div>
      <button className="pin" onClick={handleClick}>Sign In</button>
      <div className="sign-register">New to Clover App? <span>Sign up now.</span></div>
    </div>
  )
}
