import { useEffect,useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import PubSub from 'pubsub-js';
import './MovieDetail.css';

export default function MovieDetail(){
  const [movie,setMovie] = useState();
  const params = useParams();
  const navigate = useNavigate();
  useEffect(()=>{
    axios.get(`http://localhost:8888/movies/movie/${params.mid}`).then((response)=>{
      setMovie(response.data);
    })
  },[])

  const handleAdd = ()=>{
    const username = localStorage.getItem("username");
    if(!username) {
      navigate("/signin");
    }else{
      axios.post("http://localhost:8888/user/movies",{username: username+" "+movie.id}).then((response)=>{
        PubSub.publish("movie-count", response.data);
      })
    }
  }
  const handleUpdate = ()=>{
    console.log("update");

  }
  const handleDelete = ()=>{
    console.log("delete");
  }

  return (
    movie===undefined ? null :
    <div className="movie-detail">
      <h2 className="title">{movie.name}</h2>
      <div className="clearfix">
        <div className="year">{movie.releasedYear} -</div>
        <div className="minutes">{movie.minutes}m</div>
        <div className="rate">
          <div>rating:</div>
          <div>⭐️ <span>
          {
            movie.rate.toString().length<2 ?
            movie.rate+".0" :
            movie.rate
          }
          </span>/10</div>
        </div>
      </div>
      <div className="img"><img src={require(`../../../images/${movie.imgUrl}`)} alt="movie-pic" /></div>
      <div className="detail-genres">
        {
          movie.genres.map((genres) => {
            return <button key={genres.id}>
              {genres.name}
            </button>
          })
        }
      </div>
      <div className="desc">{movie.description}</div>
      <button className="pin" onClick={handleAdd}>Add to Cart</button>
      {
        localStorage.getItem("role")==="admin" ? 
        <span>
          <button className="pin update" onClick={handleUpdate}>Update</button>
          <button className="pin delete" onClick={handleDelete}>Delete</button>
        </span> :
        null
      }
    </div>
  )
}
