import { useState,useEffect } from "react";
import axios from "axios";
import "./Movie.css";

export default function Movie(){
  const [genres,setGenres] = useState(["action","comedy","drama","fantasy","horror","mystery","romance"]);
  const [action,setAction] = useState([]);
  const [comedy,setComedy] = useState([]);
  const [drama,setDrama] = useState([]);
  const [genresObj, setGenresObj] = useState({
    "action": [],
    "comedy": [],
    "drama": [],
    "fantasy": [],
    "horror": [],
    "mystery": [],
    "romance": []
  })

  useEffect(() => {
    axios.get(`http://localhost:8888/movies/genres/action`).then((response)=>{
      const newGenresObj = Object.assign({},genresObj);
      newGenresObj.action = response.data;
      console.log("genresObj", newGenresObj);
      setGenresObj(newGenresObj);
    })
    axios.get(`http://localhost:8888/movies/genres/comedy`).then((response)=>{
      const newGenresObj = Object.assign({},genresObj);
      newGenresObj.action = response.data;
      console.log("genresObj", newGenresObj);
      setGenresObj(newGenresObj);
    })
    axios.get(`http://localhost:8888/movies/genres/drama`).then((response)=>{
      const newGenresObj = Object.assign({},genresObj);
      newGenresObj.action = response.data;
      console.log("genresObj", newGenresObj);
      setGenresObj(newGenresObj);
    })
    // genres.forEach((item)=>{
    //   axios.get(`http://localhost:8888/movies/genres/${item}`).then((response)=>{
    //     const newGenresObj = Object.assign({},genresObj);
    //     newGenresObj[item] = response.data;
    //     console.log("genresObj", newGenresObj);
    //     setGenresObj(newGenresObj);
    //   })
    // })
  },[]);

  return (
    <div className="movie-container">
      {
        genres && genres.map((item)=>{
          return <div className="genres clearfix" key={item}>
            <div className="genres-name">{item}</div>
            <ul>
              {
                genresObj[item] && genresObj[item].map((movie)=>{
                  return <li key={movie.id} className="movie-list">
                    <img src={require(`../../images/${movie.imgUrl}`)} alt="movie-pic" />
                    <div>{movie.name}</div>                  
                  </li>
                })
              }
            </ul>
          </div>
        })
      }
    </div>
  )
}
